shell.send:
for ip in $($EC2_HOME/bin/ec2-describe-instances -O goal.ec2Creds.accessKey -W goal.ec2Creds.secretKey --region goal.region | grep INSTANCE | cut -f2); do 
    echo 'Sending command to terminate EC2 instance ' $ip ; 
    $EC2_HOME/bin/ec2-terminate-instances $ip -O goal.ec2Creds.accessKey -W goal.ec2Creds.secretKey --region goal.region; 
done
